mapper.update:
update board
set hit = hit + 1
where board_no = #{boardNo}

update board
set subject = #{subject}, content = #{content}, trip_no = #{tripNo}
where board_no = #{boardNo}

mapper.select:
select id, content, user_id, regist_time
from board_memo
where board_no = #{boardNo}
order by regist_time desc;
