mapper.select:
select count(user_id)
from attraction_fav
where content_id = #{contentId}

	and category = #{category}

select content_id as favId, category
from attraction_fav join attraction_info on using(content_id)
where user_id = #{userId}

	and category = #{category}

mapper.delete:
delete from attraction_fav
where 1=1
and user_id = #{userId}
and content_id = #{contentId}
and category = #{category}

mapper.select:
select count(user_id)
from board_fav
where board_no = #{boardNo}

	and category = #{category}

select board_no as favId, category
from board_fav
where user_id = #{userId}

	and category = #{category}

mapper.delete:
delete from board_fav
where 1=1
and user_id = #{userId}
and board_no = #{boardNo}
and category = #{category}
