mapper.select:
select *

	, if(lid is null, 0, 1) isLike, if(bid is null, 0, 1) isBookmark

from attraction_info a

	left outer join (
		select content_id, user_id lid from attraction_fav where user_id = #{userId} and category = 0
		) l
	using (content_id)
	left outer join (
		select content_id, user_id bid from attraction_fav where user_id = #{userId} and category = 1
		) b
	using (content_id)

where 1=1

	and sido_code = #{sidoCode}

		and gugun_code = #{gugunCode}



	and content_type_id in
	#{TypeId}


	and title like concat('%',#{keyword},'%')


	order by (#{mapX} - latitude) * (#{mapX} - latitude) +
	(#{mapY} - longitude) * (#{mapY} - longitude)

limit 30;

select id, content, user_id,
regist_time
from attraction_memo
where content_id = #{contentId}
order by
regist_time desc;
